const App = () => {


  const data = {
    key : "AIzaSyA9x7L8UieoJDD0R5TA7rMqlPwRMPgIlM0",
    title: 'React POST Request Example'
  }

      // Simple POST request with a JSON body using fetch
    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data)
    };

  const onClick = async () => {
  await fetch("https://script.google.com/macros/s/AKfycbx2o79286xYQg2qxUolnmwLP2IxARu2eAVzozxBYjskwsCKZsmdRTD8M5dhhISLRgPl/exec",
   requestOptions
  ).then(
    (response) => 
    console.log(response.json()))
  .catch((error) => {
    console.log(`error: ${error}`)
  }

  )
  }


return (
      <div>
        <button onClick={onClick}>결과 확인</button> 
        <div></div>
      </div>
  );

}

export default App;
