import React , {useState, useCallback} from 'react';


const Insert = () => {

    const [title, setTitle] = useState();
    const [targetPeriod, setTargetPeriod] = useState();

    const onSubmit = () => {

        setTitle('');
        setTargetPeriod('');

    }
    return (


        <form>
            <h3>&lt;입력란&gt;</h3><br/>
          제목 : <input name='title' type = 'text' placeholder='제목을 입력 하세요.' value={title}/>
          주기 : <input name='targetPeriod' type = 'text' placeholder='주기를 숫자로 입력하세요' value={targetPeriod} />
          <button type='submit' onClick={onSubmit}>생성</button>
        </form>



    )
}

export default Insert;
